  methods: {
    goToCharacters() {
      this.$router.push(`/characters/`)
    },
    addCharacter() {
      const characterCalc = characters.find(character => character.gameId == this.id);
      const elementMap = {
        Water: 'const-circle-hydro',
        Grass: 'const-circle-dendro',
        Electric: 'const-circle-electro',
        Fire: 'const-circle-pyro',
        Wind: 'const-circle-anemo',
        Rock: 'const-circle-geo',
        Ice: 'const-circle-cryo'
      };
      return elementMap[characterCalc.costElemType] || '';
    },
    takeElement() {
      const characterCalc = characters.find(character => character.gameId == this.id);
      const elementMap = {
        Water: 'Гидро',
        Grass: 'Дендро',
        Electric: 'Электро',
        Fire: 'Пиро',
        Wind: 'Анемо',
        Rock: 'Гео',
        Ice: 'Крио'
      };
      return elementMap[characterCalc.costElemType] || '';
    },
    getTotalCritRate(jsonData) {
      let totalCritRate = 0;

      for (let i = 0; i < jsonData.artifacts.length; i++) {
        const artifact = jsonData.artifacts[i];
        artifact.sub_stats.forEach((stat) => {
          if (stat.name === "CRIT Rate") {
            totalCritRate += stat.value * 2;
          }
        });
      }

      for (let i = 0; i < jsonData.artifacts.length; i++) {
        const artifact = jsonData.artifacts[i];
        artifact.sub_stats.forEach((stat) => {
          if (stat.name === "CRIT DMG") {
            totalCritRate += stat.value;
          }
        });
      }

      jsonData.artifacts.forEach((artifact) => {
        if (artifact.main_stat_name === "CRIT Rate") {
          totalCritRate += artifact.main_stat_value * 2;
        }
      });

      jsonData.artifacts.forEach((artifact) => {
        if (artifact.main_stat_name === "CRIT DMG") {
          totalCritRate += artifact.main_stat_value;
        }
      });

      return totalCritRate.toFixed(1);
    },
    highlightStats(statName) {
      const statElements = document.querySelectorAll(`[data-stat="${statName}"]`);
      statElements.forEach(element => {
        element.classList.add('highlight');
      });
    },
    removeHighlight(statName) {
      const statElements = document.querySelectorAll(`[data-stat="${statName}"]`);
      statElements.forEach(element => {
        element.classList.remove('highlight');
      });
    },
    checkArtifacts() {

      const chosenCharacter = this.enkacharacters.find(character => character.id == this.id);

      const data = {
        artifact1: chosenCharacter.artifacts[0].set,
        artifact2: chosenCharacter.artifacts[1].set,
        artifact3: chosenCharacter.artifacts[2].set,
        artifact4: chosenCharacter.artifacts[3].set,
        artifact5: chosenCharacter.artifacts[4].set,
      };

      const artifactCounts = {};
      for (const artifact of Object.values(data)) {
        artifactCounts[artifact] = (artifactCounts[artifact] || 0) + 1;
      }
      const counts = Object.values(artifactCounts);
      if (counts.includes(4)) {
        const artifacts = Object.keys(artifactCounts).filter(artifact => artifactCounts[artifact] === 4);
        return artifacts.join("  "); // 4 of any one artifact
      } else if (counts.includes(2) && counts.includes(2) && counts.length === 2) {
        const artifacts = Object.keys(artifactCounts).filter(artifact => artifactCounts[artifact] === 2);
        return artifacts.join("  ") + " "; // 2 of 1 artifacts
      } else if (counts.includes(2) && counts.includes(2) && counts.includes(1) && counts.length === 3) {
        const artifacts = Object.keys(artifactCounts).filter(artifact => artifactCounts[artifact] === 2);
        return artifacts.join("  ") + " "; // 2 of 2 artifacts
      } else {
        return "Без сета артефактов";
      }
    },
    checkNumArtifacts() {

      const chosenCharacter = this.enkacharacters.find(character => character.id == this.id);

      const data = {
        artifact1: chosenCharacter.artifacts[0].set,
        artifact2: chosenCharacter.artifacts[1].set,
        artifact3: chosenCharacter.artifacts[2].set,
        artifact4: chosenCharacter.artifacts[3].set,
        artifact5: chosenCharacter.artifacts[4].set,
      };

      const artifactCounts = {};
      for (const artifact of Object.values(data)) {
        artifactCounts[artifact] = (artifactCounts[artifact] || 0) + 1;
      }
      const counts = Object.values(artifactCounts);
      if (counts.includes(4)) {
        return 4; // 4 of any one artifact
      } else if (counts.includes(2) && counts.includes(2) && counts.length === 2) {
        return 21; // 2 of two artifacts
      } else if (counts.includes(2) && counts.includes(2) && counts.includes(1) && counts.length === 3) {
        return 22; // 2 of three artifacts
      } else {
        return 0;
      }
    },
    getRollsCount(value) {
      const values = [209.130, 239.000, 268.880, 298.750];
      let remainingValue = parseFloat(value.toFixed(1));
      let rolls = 0;
      for (let i = values.length - 1; i >= 0; i--) {
        while (remainingValue >= values[i]) {
          remainingValue -= values[i];
          rolls++;
        }
      }
      return rolls;
    },
    getNearestRollsSum(value) {
      const values = [209.130, 239.000, 268.880, 298.750];
      let remainingValue = parseFloat(value.toFixed(1));
      let rollsSum = 0;
      for (let i = values.length - 1; i >= 0; i--) {
        while (remainingValue >= values[i]) {
          remainingValue -= values[i];
          rollsSum += values[i];
        }
      }
      return rollsSum;
    },
    getRollsValues(value) {
      const values = [209.130, 239.000, 268.880, 298.750];
      let remainingValue = parseFloat(value.toFixed(1));
      let rolls = [];
      for (let i = values.length - 1; i >= 0; i--) {
        while (remainingValue >= values[i]) {
          remainingValue -= values[i];
          rolls.push(values[i]);
        }
      }
      return rolls;
    },
    sliderMax(value, type) {
      // вставляет макс ролл в слайдер
      if (value === 'CRIT Rate') {
        return 23.3;
      } else if (value === 'ATK') {
        if (type === 1) {
          // возвращаем значение для ATK с процентом
          return 35.0;
        } else if (type === 0) {
          // возвращаем значение для ATK без процента
          return 117;
        }
      } else if (value === 'CRIT DMG') {
        return 46.6;
      } else if (value === 'Energy Recharge') {
        return 38.9;
      } else if (value === 'DEF') {
        return 139;
      } else if (value === 'DEF%') {
        return 43.7;
      } else if (value === 'HP') {
        return 1793;
      } else if (value === 'HP%') {
        return 35.0;
      } else if (value === 'Elemental Mastery') {
        return 140;
      }
    },
    sliderMin(value) {
      // вставляет мин ролл в слайдер
      if (value === 'CRIT Rate') {
        return 2.72;
      } else if (value === 'ATK') {
        return 13.620;
      } else if (value === 'CRIT DMG') {
        return 5.44;
      } else if (value === 'Energy Recharge') {
        return 4.53;
      } else if (value === 'DEF') {
        return 16.200;
      } else if (value === 'DEF%') {
        return 5.10;
      } else if (value === 'HP') {
        return 209.130;
      } else if (value === 'HP%') {
        return 4.08;
      } else if (value === 'ATK%') {
        return 4.08;
      } else if (value === 'Elemental Mastery') {
        return 16.320;
      }
    },
    valueSelectedSlider(value) {
      // calculate the 0.1 or 1 value based on the selected value
      if (value === 'CRIT Rate') {
        return 0.1;
      } else if (value === 'ATK') {
        return 1;
      } else if (value === 'CRIT DMG') {
        return 0.1;
      } else if (value === 'Energy Recharge') {
        return 0.1;
      } else if (value === 'DEF') {
        return 1;
      } else if (value === 'DEF%') {
        return 0.1;
      } else if (value === 'HP') {
        return 1;
      } else if (value === 'HP%') {
        return 0.1;
      } else if (value === 'ATK%') {
        return 0.1;
      } else if (value === 'Elemental Mastery') {
        return 1;
      }
    },
  },
  computed: {
    chosenCharacter() {
      return this.enkacharacters.find(character => character.id == this.id);
    },
  },
}
